MODULE="shade"
TITLE=${MODULE^^}

fn_shade() {
	cat <<EOF >"${OUTPUT_DIR}/${MODULE}.html"
<!DOCTYPE html>
<html lang="en" >
<head>
	<title>${TITLE}</title>
	<meta charset="UTF-8">
	<meta name="viewport" content="width=device-width, initial-scale=1">
    <meta charset="utf-8">
    <meta name="description" content="Shaded Text, a SVG+CSS3 experiment about animated shadows by RGG">
    <meta content="IE=edge" http-equiv="X-UA-Compatible">
	<link href="https://cdnjs.cloudflare.com/ajax/libs/meyer-reset/2.0/reset.min.css" rel="stylesheet">
<style>
@charset 'utf-8';
 
 @import 'https://fonts.googleapis.com/css?family=Open+Sans:300,700';
 @import 'https://fonts.googleapis.com/css?family=Kreon:300,700';
 @font-face {
	 font-family: 'dragon_is_comingregular';
	 src: url(data:application/font-woff2;
	charset=utf-8;
	base64,) format('woff2'), url(data:application/font-woff;
	charset=utf-8;
	base64,) format('woff');
	 font-weight: normal;
	 font-style: normal;
}
 html, body {
	 height: 100%;
	 width: 100%;
	 box-sizing: border-box;
}
 body {
	 background-color: ${BCOLOR};
	 color: #444;
	 text-align: center;
	 font-family: 'Open Sans';
	 font-weight: 300;
	 line-height: 1em;
	 display: flex;
	 align-items: center;
	 justify-content: center;
	 flex-direction: column;
}
 h1 {
	 font-size: 2.5em;
	 margin: 2em 0 0.5em;
	 font-family: 'Kreon', serif;
	 font-weight: 300;
}
 em, strong {
	 font-weight: 700;
}
 .main {
	 width: 100%;
	 height: 100%;
	 display: flex;
	 justify-content: center;
	 align-items: center;
}
 header {
	 margin-bottom: 1em;
}
 .svg-text {
	 
	 transform: rotateX(30deg);
	 font-size: ${SIZE}vw;
	 font-family: 'dragon_is_comingregular', serif;
	 transition: all 0.6s ease-in-out;
	 color: #f0f0f0;
}
 .svg-text__shaded {
	 text-shadow: 0 1px 1px rgba(33, 33, 33, .15), 0 3px 10px rgba(33, 33, 33, .15), 0 3px 20px rgba(33, 33, 33, .35);
}
 .svg-text__shaded__sub {
	 font-size: 0.05em;
	 font-family: 'Open Sans', sans-serif;
}
 .svg-text__shaded__stroke {
	 stroke-dasharray: 3em 0.5em;
	 stroke-dashoffset: 0;
	 transition: all 0.6s ease-in-out;
}
 .svg-text .bold {
	 font-size: 0.5em;
	 font-family: 'Kreon', sans-serif;
	 font-weight: 700;
}
 .svg-text:hover {
	 cursor: pointer;
}
 .svg-text:hover .svg-text__shaded__stroke {
	 animation: offsetAnim 4.2s ease-in-out infinite;
}
 .hover {
	 display: inline-block;
	 color: white;
	 font-size: 8em;
	 font-family: 'dragon_is_comingregular';
	 text-align: left;
	 text-shadow: 0 0.005em 0 #e6e6e6, 0 0.01em 0 #e6e6e6, 0 0.015em 0 #e6e6e6, 0 0.02em 0 #e6e6e6, 0 0.025em 0 #e6e6e6, 0 0.025em 0.02em rgba(33, 33, 33, .25), 0 0.028em 0.2em rgba(33, 33, 33, .45);
	 transform-origin: 50% 50%;
	 transform: translate(1.75em, 0.45em) rotate(30deg);
}
 @keyframes offsetAnim {
	 70%, 100% {
		 stroke-dashoffset: 3.5em;
	}
}
 
</style>
</head>
<body>
<main class="main">
        <svg class="svg-text" width="100%" height="1.5em" xmlns="http://www.w3.org/2000/svg">
            <defs>
                <symbol id="stroke-2"><text class="bold" x="50%" y="67%" fill="none" stroke-width=".1em" stroke-linecap="round" stroke-linejoin="round" paint-order="stroke fill" text-anchor="middle">${TEXT}</text></symbol>
                <symbol id="fill-2"><text class="bold" x="50%" y="60%" text-anchor="middle">${TEXT}</text></symbol>
            </defs>
            <g class="svg-text__shaded__stroke" stroke="#00cccc">
                <use y="5%" xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#stroke-2" opacity="0.5" filter="url(#drop-stroke-shadow)"></use>
                <use y="3%" xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#stroke-2"></use>
                <use y="2%" xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#stroke-2"></use>
                <use y="1%" xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#stroke-2"></use>
                <use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#stroke-2" stroke="cyan"></use>
            </g>
            <g fill="#e6e6e6">
                <use class="svg-text__shaded" y="7%" xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#fill-2"></use>
                <use y="6.5%" xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#fill-2"></use>
                <use y="6%" xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#fill-2"></use>
                <use y="5.5%" xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#fill-2"></use>
                <use y="5%" xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#fill-2"></use>
                <use y="4.5%" xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#fill-2"></use>
                <use y="4%" xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#fill-2"></use>
                <use y="3.5%" xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#fill-2"></use>
                <use y="3%" xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#fill-2" fill="white"></use>
            </g>
        </svg>
  
</main>
</body>
</html>
EOF

	# check if $RETRO_OUTPUT exist
	if [ ! -d "$OUTPUT_DIR" ]; then
		mkdir -p "$OUTPUT_DIR"
	fi
	eval "$FIREFOX" --headless --screenshot "${OUTPUT_DIR}/${MODULE}.png" "file:///${OUTPUT_DIR}/${MODULE}.html" --window-size="${WIDTH},${HEIGHT}" >/dev/null 2>&1 || {
		echo "Something went wrong."
		exit
	}

	if [ "$IMAGE" = 1 ]; then
		echo "Opening $OUTPUT_DIR/${MODULE}.png ..."
		# open browser
		if [[ $(uname) == "Linux" ]]; then
			xdg-open "$OUTPUT_DIR/${MODULE}.png" >/dev/null 2>&1
		elif [[ $(uname) == "Darwin" ]]; then
			open "$OUTPUT_DIR/${MODULE}.png" >/dev/null 2>&1
		fi
		echo "The image location is $OUTPUT_DIR/${MODULE}.png."
	fi

	if [ "$BROWSER" = 1 ]; then
		# open browser
		if [[ $(uname) == "Linux" ]]; then
			xdg-open "file:///${OUTPUT_DIR}/${MODULE}.html" >/dev/null 2>&1
		elif [[ $(uname) == "Darwin" ]]; then
			open "file:///${OUTPUT_DIR}/${MODULE}.html" >/dev/null 2>&1
		fi
		echo "${MODULE} image is on a browser."
	fi
}
